import Input from './components/Input';
import Button from './components/Button';
import { Container, Content, Row, Column } from './styles';
import { useState } from 'react';

const App = () => {

  //#region Hook useState setCurrentNumber
  const [number, setCurrentNumber] = useState('0');

  const addCurrentNumber = (n) => {
    setCurrentNumber(prev => prev === '0' ? n : `${prev}${n}`)
  }

  const clear = () => {
    setCurrentNumber('0');
    setFirstNumber('0');
  }

  //#endregion

  //#region Hook useState SetFirstNumber 
  const [firstNumber, setFirstNumber] = useState('0');

  const sumNumbers = () => {

    if (firstNumber === '0'){
      setCurrentNumber('0');
    } else {
      const sum = Number(firstNumber) + Number(number);
      setCurrentNumber(String(sum));
      number = '0';
    }
  }

  //#endregion

  return (
    <Container>
      <Content>
        <Input value={number}/>
        <Row>
          <Button label="x"/>
          <Button label="/"/>
          <Button label="C" onClick={clear}/>
          <Button label="(]"/>
        </Row>
        <Row>
          <Button label="7" onClick={() => addCurrentNumber('7')}/>
          <Button label="8" onClick={() => addCurrentNumber('8')}/>
          <Button label="9" onClick={() => addCurrentNumber('9')}/>
          <Button label="-"/>
        </Row>
        <Row>
          <Button label="4" onClick={() => addCurrentNumber('4')}/>
          <Button label="5" onClick={() => addCurrentNumber('5')}/>
          <Button label="6" onClick={() => addCurrentNumber('6')}/>
          <Button label="+" onClick={sumNumbers}/>
        </Row>
        <Row>
          <Button label="1" onClick={() => addCurrentNumber('1')}/>
          <Button label="2" onClick={() => addCurrentNumber('2')}/>
          <Button label="3" onClick={() => addCurrentNumber('3')}/>
          <Button label="="/>
        </Row>
      </Content>
    </Container>
  );
}

export default App;
