import styled from 'styled-components';

export const ButtonContainer = styled.button `
    padding: 20px;
    border: 1px solid #dcdcdc;
    font-size: 24px;
    background-color: #FFF;
    font-weight: 700;
    flex: 1;

    &:hover {
        background-color: #ff3064;
        transition: 0.5s;
        border: 1px solid #fff;
    }
`
