import Input from './components/Input';
import Button from './components/Button';
import { Container, Content, Row, Column } from './styles';
import { useState } from 'react';

const App = () => {

  //#region Hook useState SetNumber
  const [number, setNumber] = useState('0');

  const addNumber = (n) => {
    setNumber(prev => prev === '0' ? n : `${prev}${n}`)
  }

  const clear = () => {
    setNumber('0');
    setFirstNumber('0');
  }
  //#endregion

  //#region Hook useState SetFirstNumber 
  const [firstNumber, setFirstNumber] = useState('0');

  const sumNumbers = () => {

    if (firstNumber === '0'){
      setFirstNumber(String(number));
      setNumber('0');
    } else {
      const sum = Number(firstNumber) + Number(number);
      setNumber(String(sum));
    }
  }

  //#endregion

  return (
    <Container>
      <Content>
        <Input value={number}/>
        <Row>
          <Button label="x"/>
          <Button label="/"/>
          <Button label="C" onClick={clear}/>
          <Button label="(]"/>
        </Row>
        <Row>
          <Button label="7" onClick={() => addNumber('7')}/>
          <Button label="8" onClick={() => addNumber('8')}/>
          <Button label="9" onClick={() => addNumber('9')}/>
          <Button label="-"/>
        </Row>
        <Row>
          <Button label="4" onClick={() => addNumber('4')}/>
          <Button label="5" onClick={() => addNumber('5')}/>
          <Button label="6" onClick={() => addNumber('6')}/>
          <Button label="+" onClick={sumNumbers}/>
        </Row>
        <Row>
          <Button label="1" onClick={() => addNumber('1')}/>
          <Button label="2" onClick={() => addNumber('2')}/>
          <Button label="3" onClick={() => addNumber('3')}/>
          <Button label="="/>
        </Row>
      </Content>
    </Container>
  );
}

export default App;
