import styled from 'styled-components';

export const InputContainer = styled.div `
    width: 100%;
    height: 75px;
    background-color: #FF3064; 

    display: flex;
    align-items: center;
    justify-content: flex-end;

    font-size: 24px;
    font-family: 'Consolas'; 

    input {
        width: 100%;
        height: 75px;
        background-color: #FF3064;
        border: 0;
        padding: 0 10px;

        font-size: 24px;
        font-family: 'Consolas'; 
    }
`
