import styled from 'styled-components';

export const InputContainer = styled.div `
    width: 100%;
    height: 75px;
    background-color: #AAFFAA;

    display: flex;
    align-items: center;
    justify-content: flex-end;

    font-size: 24px;
    font-family: 'Consolas'; 

    input {
        width: 100%;
        height: 75px;
        background-color: #AAFFAA;
        border = 0;
        display: flex;
        justify-content: flex-end;

        font-size: 24px;
        font-family: 'Consolas'; 
    }
`
