import styled from 'styled-components';

export const Container = styled.div`
    width: 100%;
    height: 100vh;
    background-color: #dadada;

    display: flex;
    align-items: center;
    justify-content: center;
`

export const Content = styled.div `
    background-color: #fff;
    width: 80%;
    min-height: 350px;
    box-shadow: 3px 3px 5px #7c7c7c;
`
